import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import validateForm from 'src/app/helpers/validateForm';
import { AuthService } from 'src/app/services/auth.service';
import {Router} from '@angular/router';
import { NgToastService } from 'ng-angular-popup';
import { UserStoreService } from 'src/app/services/user-store.service';





@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})

export class LoginComponent {



  type:string = "password"
  isText: boolean = false;
  eyeIcon:string = "fa-eye-slash";

  loginForm!:FormGroup;
  constructor(private fb: FormBuilder, private auth: AuthService ,private router: Router, private toast:NgToastService,private userStore:UserStoreService) { }

  ngOnInit(): void{
    this.loginForm= this.fb.group({
      username: ['',Validators.required],
      password: ['',Validators.required]

    })


  }

  hideshowpass(){
     this.isText = !this.isText;
     this.isText?this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
     this.isText?this.type = "text" : this.type = "password";  
  } 



  async onLogin(){
    if(this.loginForm.valid)
    {

     //send data to database     
     this.auth.login(this.loginForm.value)
     .subscribe({
      next:(res=>{
        console.log(res.message) ;  
        this.loginForm.reset();
        this.auth.storeToken(res.accessToken)
        this.auth.storeRefreshToken(res.refreshToken)
        this.toast.success({detail:"Success",summary:res.message,duration :5000}) ;
        const tokenPayload = this.auth.decodeToken();
        this.userStore.setFullNameForStore(tokenPayload.unique_name);
        this.userStore.setRoleForStore(tokenPayload.role);
        this.router.navigate(['landing']);


      }),
      error:(err=>{

        this.toast.error({detail:"ERROR",summary:"Something went wrong!",duration :5000}) ; 

      })
     })


    }
    else{
      console.log("form is not valid!");

      validateForm.validateAllFormFields(this.loginForm);
      alert("Your form is invalid!") ; 
    }   
  }

}

<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Login</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form [formGroup]= "loginForm">
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="loginForm.controls['username'].dirty && loginForm.hasError('required','username')">
                                    <span class="fa fa-user p-2">                                         
                                    </span>
                                    <input formControlName="username" type="text" placeholder="Username">
                                </div>
                            </div>
                            <small *ngIf= "loginForm.controls['username'].dirty && loginForm.hasError('required','username')" class="text-danger">*Username is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="loginForm.controls['password'].dirty && loginForm.hasError('required','password')">
                                    <span class="fa fa-lock p-2">                                         
                                    </span>
                                    <input formControlName="password" [type]="type" placeholder="Password">
                                    <span (click) ="hideshowpass()" class="fa {{eyeIcon}}"></span>
                                </div>                                                                
                            </div>
                            <small *ngIf= "loginForm.controls['password'].dirty && loginForm.hasError('required','password')" class="text-danger" >*Password is required</small>

                            <div class="form-inline fr mt-4">
                                <a id = "forget" class="font-weight-bold">Forget Password ?</a>
                            </div>

                            <button (click) ="onLogin()"  type="submit" class="btn btn-primary mt-4 w-100">Login</button>
                            <div class="text-center pt-4 text-muted">Don't have account? <a id="forget" routerLink='/signup'>Signup</a></div>
                        </form>
                    </div>
                    <div class="mx-3 my-2 py-2 bordert">
                        <div class="text-center pu-3">
                            <a href="#" class="px-2"><img src="https://www.dpreview.com/files/p/articles/4698742202/facebook.jpeg"  alt=""></a>
                            <a href="#" class="px-2"><img src="https://www.freepnglogos.com/uploads/google-logo-png/google-logo-png-suite-everything-you-need-know-about-google-newest-0.png"  alt=""></a>
                            <a href="#" class="px-2"><img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRIPPlnUnhPRbSLfZr0TctDI3RN7mB7hqw8aqJjMCZaS9NvUQk&s"  alt=""></a>
                        </div>
                    </div>
                </div>
            </div> 
        </div>
    </div>
</div>
